+    UpdatedAt time.Time      `json:"updated_at"`
+    StorageKey string        `json:"storage_key"`
+}
+
+func toContentResponse(c *ent.Content) contentResponse {
+    return contentResponse{
+        ID:         c.ID,
+        Name:       c.Name,
+        MimeType:   c.MimeType,
+        SizeBytes:  c.SizeBytes,
+        Status:     c.Status,
