+    if input.ContentID != nil {
+        update.SetContentID(*input.ContentID)
+    }
+    if input.Data != nil {
+        update.SetData(input.Data)
+    }
+    if input.DurationSecs > 0 {
+        update.SetDurationSeconds(input.DurationSecs)
+    }
+    update.SetUpdatedAt(time.Now())
