+        return err
+    }
+    if len(modules) != len(moduleIDs) {
+        return ErrInvalidInput
+    }
+
+    index := make(map[uuid.UUID]int, len(moduleIDs))
+    for pos, id := range moduleIDs {
+        index[id] = pos
+    }
+
+    for _, m := range modules {
+        pos, ok := index[m.ID]
